$(document).ready(function(){$("[id*=sbkThumbnailImg]").each(function(){var n=$(this),t=n.attr("id").split("g")[1],u=$("#descThumbnail"+t).html(),i="center left",r="center right";t%4==0&&(i="center right",r="center left");n.qtip({content:{text:u},position:{my:i,at:r},style:{width:500,border:1,hide:{fixed:!0,delay:1e3},classes:"qtip-tipsy",tip:{width:20}}})})})